import {useEffect, useState} from "react";
import homePageService from "../services/homePageService";
import AuthService from "../services/auth.service";
import Container from "react-bootstrap/Container";
import {Toast, ToastContainer} from "react-bootstrap";
import SongBox from "./SongBox";
import searchResultPageService from "../services/searchResultPageService";
import {useLocation} from "react-router";

export default function SearchResultPage() {
    const [songs, setSongs] = useState(undefined)
    const [artist, setArtist] = useState("")
    const [genre, setGenre] = useState("")
    const [rating, setRating] = useState("")
    const {search} = useLocation();
    const params = new URLSearchParams(search);

    useEffect(() => {

        setArtist(params.get("artist"))
        setGenre(params.get("genre"))
        setRating(params.get("rating"))
        console.log([params.get("artist"),params.get("genre"),params.get("rating")])


        searchResultPageService.searchSongs(params.get("artist"), params.get("genre"),params.get("rating") ).then(response => {
            console.log(response.data.result)
            setSongs(response.data.result)
        })
            .catch(e => {
                console.log(e)
            });


    }, []);

    return (
        <>
            <Container>
                <h1>Search Result</h1>
                {artist.length > 0 && <h3 muted>Artist: {artist}</h3>}
                {genre.length > 0 && <h3>Genre: {genre}</h3>}
                {rating.length > 0 && <h3>Rating: > {rating}</h3>}
                <Container className="justify-content-around  d-flex flex-wrap gap-1" >
                    {songs && songs.map(song=>{
                        return(<SongBox song={song} key={song.songID}></SongBox>)
                    })}
                </Container>

            </Container>
        </>
    )
}
