import React, {useEffect, useRef, useState} from "react";
import friendPageService from "../services/friendPageService";
import {Accordion, Button, Card, Form, InputGroup, Table} from "react-bootstrap";
import {Alert, Collapse, IconButton} from "@mui/material";
import CloseIcon from '@mui/icons-material/Close';
import * as PropTypes from "prop-types";



CloseIcon.propTypes = {fontSize: PropTypes.string};
export default function FriendPage() {
    const [friends, setFriends] = useState(undefined);
    const [requestsTo, setRequestsTo] = useState(undefined);
    const [searchUser, setSearchUser] = useState("");
    const [searchResult, setSearchResult] = useState(undefined);
    const [validated, setValidated] = useState(false);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");
    const showSearchTable = searchResult && searchResult.length !== 0
    const showRequestToTable = requestsTo && requestsTo.length !== 0
    const showFriendTable = friends && friends.length !== 0;
    const form = useRef();
    useEffect(() => {
        friendPageService.getFriends().then(response => {
            console.log(response.data.result)
            setFriends(response.data.result)

        })
            .catch(e => {
                console.log(e)
            });
        friendPageService.getRequestsTo().then(response => {
            console.log(response.data.result)
            setRequestsTo(response.data.result)

        })
            .catch(e => {
                console.log(e)
            });


    }, []);

    const onChangeUsername = (e) => {
        const username = e.target.value;
        setSearchUser(username);
    };

    const handleSearch = (e) => {
        setSearchResult(undefined)
        e.preventDefault();
        setMessage("");
        setLoading(true);
        if (form.current.checkValidity() === false) {
            e.stopPropagation();
            setLoading(false);
            setValidated(true)
            return
        }
        friendPageService.searchUser(searchUser).then(response => {
                console.log(response.data.result);
                setLoading(false);
                setSearchResult(response.data.result)
            },
            (error) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.error &&
                        error.response.data.error.info) ||
                    error.message ||
                    error.toString();

                setLoading(false);
                setMessage(resMessage);
            })
    };


    return (
        <>
            <h1> Friends</h1>
            <Card border="light" className="rounded-3">
                <Card.Header as="h5">Send a Request</Card.Header>
                <Card.Body className="d-flex flex-row justify-content-start">
                    <Form className="d-flex w-50 me-3" ref={form} onSubmit={handleSearch} noValidate
                          validated={validated}>
                        <InputGroup hasValidation
                                    style={{minWidth: "150px", maxWidth: "250px", height: "30px"}}>

                            <Form.Control
                                type="text"
                                placeholder="Please input a username"
                                aria-describedby="inputGroupPrepend"
                                className="me-2 ms-2"
                                required
                                onChange={onChangeUsername}

                            />
                            <Form.Control.Feedback type="invalid" className="  me-2 ms-5">
                                Please input a username!
                            </Form.Control.Feedback>
                        </InputGroup>
                        <Button variant="outline-success" type="submit" disabled={loading}
                                className="d-inline-block"
                                style={{height: "40px"}}>
                            {loading ? (
                                <span className="spinner-border spinner-border-sm"></span>
                            ) : "Search"}
                        </Button>
                        {message && (
                            <div className="form-group">
                                <div className="alert alert-danger" role="alert">
                                    {message}
                                </div>
                            </div>
                        )}
                    </Form>
                    {showSearchTable ?
                        <Table striped bordered hover size="sm" className="">
                            <thead>
                            <tr>
                                <th> #</th>
                                <th>Username</th>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Nick Name</th>

                            </tr>
                            </thead>
                            <tbody>
                            {
                                searchResult.map((user, index) => (<tr key={user.username}>
                                        <td>{index}</td>
                                        <td>{user.username}</td>
                                        <td>{user.fname}</td>
                                        <td>{user.lname}</td>
                                        <td>{user.nickname}</td>
                                        <td className="d-flex justify-content-center">
                                            <Button variant="outline-danger" className="p-1 m-1">Request</Button>
                                        </td>
                                    </tr>)
                                )
                            }


                            </tbody>
                        </Table> :

                        <Collapse in={searchResult !== undefined}>
                            <Alert
                                variant="outlined" severity="info" color="error"
                                action={
                                    <IconButton
                                        aria-label="close"
                                        color="inherit"
                                        size="small"
                                        onClick={() => {
                                            setSearchResult(undefined);
                                        }}
                                    >
                                        <CloseIcon fontSize="inherit"/>
                                    </IconButton>
                                }
                                sx={{mb: 2}}
                            >
                                No Result
                            </Alert>
                        </Collapse>
                    }
                </Card.Body>
            </Card>


            <Card border="light" className="rounded-3">
                <Card.Header as="h5">New Requests To You</Card.Header>
                <Card.Body className="overflow-auto">
                    {showRequestToTable ?  <Table striped bordered hover size="sm">
                        <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Username</th>
                        </tr>
                        </thead>
                    </Table> : <Alert className="w-25" variant="outlined" severity="info" color="info">No new requests to you.</Alert>
                    }


                </Card.Body>
            </Card>

            <Card border="light" className="rounded-3">
                <Card.Header as="h5">Friend List</Card.Header>
                <Card.Body className="overflow-auto">
                    {showFriendTable ?  <ListGroup as="ol" numbered>
                        <ListGroup.Item as="li">Cras justo odio</ListGroup.Item>
                        <ListGroup.Item as="li">Cras justo odio</ListGroup.Item>
                        <ListGroup.Item as="li">Cras justo odio</ListGroup.Item>
                    </ListGroup> : <Alert className="w-25" variant="outlined" severity="info" color="info">No friends yet. Try to send a new request!</Alert>
                    }

                </Card.Body>
            </Card>


        </>
    )
}
