import React, {useEffect, useRef, useState} from "react";
import friendPageService from "../services/friendPageService";
import {Button, Card, Form, InputGroup} from "react-bootstrap";


export default function FriendPage(){
    const [friends, setFriends] = useState(undefined);
    const [searchUser, setSearchUser] = useState("");
    const [validated, setValidated] = useState(false);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");
    const form = useRef();
    useEffect(() => {
        friendPageService.getFriends().then(response => {
            console.log(response.data.result)
            setFriends(friends)

        })
            .catch(e => {
                console.log(e)
            });
        ;


    }, []);

    const onChangeUsername = (e) => {
        const username = e.target.value;
        setSearchUser(username);
    };

    const handleSearch = (e) => {
        e.preventDefault();
        setMessage("");
        setLoading(true);
        if (form.current.checkValidity() === false) {
            e.stopPropagation();
            setLoading(false);
            setValidated(true)
            return
        }
        friendPageService.searchUser(searchUser).then(response=>{console.log(response)}, (error) => {
            const resMessage =
                (error.response &&
                    error.response.data &&
                    error.response.data.error &&
                    error.response.data.error.info) ||
                error.message ||
                error.toString();

            setLoading(false);
            setMessage(resMessage);
        })
    };

    return(
        <>
            <h1> Friends</h1>
            <Card>
                <Card.Header as="h5">New Request</Card.Header>
                <Card.Body>
                    <Form className="d-flex" ref={form} onSubmit={handleSearch} noValidate validated={validated}>
                        <InputGroup hasValidation>

                            <Form.Control
                                type="text"
                                placeholder="Please input a username"
                                aria-describedby="inputGroupPrepend"
                                className="me-2"
                                required
                                onChange={onChangeUsername}
                            />
                            <Form.Control.Feedback type="invalid">
                                Please input a username.
                            </Form.Control.Feedback>
                        </InputGroup>
                        <Button variant="outline-success" type="submit" disabled={loading} className="d-inline-block" >Search
                        {loading && (
                            <span className="spinner-border spinner-border-sm"></span>
                        )}
                        </Button>
                        {message && (
                            <div >
                                <div className="alert alert-danger" role="alert">
                                    {message}
                                </div>
                            </div>
                        )}
                    </Form>
                </Card.Body>
            </Card>
        </>
    )
}
