import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import {Button, Form, Nav, NavDropdown, OverlayTrigger, Popover} from "react-bootstrap";
import logo from '../resources/logo.png';
import theme from "../common/Theme";
import NavbarCollapse from "react-bootstrap/NavbarCollapse";
import NavbarToggle from "react-bootstrap/NavbarToggle";
import {Routes, Route, Link, useNavigate} from "react-router-dom";
import {useEffect, useState} from "react";
import AuthService from "../services/auth.service";
import EventBus from "../common/EventBus";
import PersonRoundedIcon from '@mui/icons-material/PersonRounded';
import Image from 'react-bootstrap/Image';

export default function Header({user}) {
    let navigate = useNavigate();

    function logout() {
        AuthService.logout()
        navigate("/");
        window.location.reload();
    }


    const nav = user ? (<Nav>
        <OverlayTrigger
            trigger="click"
            placement="bottom"
            overlay={
                <Popover>
                    <Popover.Header as="h3">{`Log Out`}</Popover.Header>
                    <Popover.Body>
                        Are you going to <strong className="d-inline-block border-danger border border-2 p-1 rounded-2">log out</strong> ?
                        <Button className="alert-danger bt-sm me-2" variant="danger" onClick={logout}>
                            Log Out
                        </Button>
                    </Popover.Body>
                </Popover>
            }
        >

            <Button
                variant="light"
                className="d-inline-flex align-items-center btn-sm"
            >
                <PersonRoundedIcon/>
                <span className="ms-1 fw-bold fs-5">{user}</span>
            </Button>

        </OverlayTrigger>
    </Nav>) : (<Nav>
        <Nav.Link href="/login">
            Log in
        </Nav.Link>
        <Nav.Link href="/register">
            Register
        </Nav.Link>
    </Nav>);
    return (
        <>

            <Navbar expand="md" style={{backgroundColor: theme.dark}} variant="dark">

                <Container className="col-12" style={{maxWidth: "none"}}>

                    <Navbar.Brand style={{overflow: "hidden"}}>

                        <img src={logo} alt="fatEar " width="60" className="d-inline-block align-top" style={{
                            marginLeft: 30,
                            position: "relative",
                            left: "-10px",
                            transform: "scale(2.2)"
                        }}/>

                    </Navbar.Brand>

                    <NavbarToggle aria-controls="responsive-navbar-nav"
                                  style={{backgroundColor: theme.peach}}></NavbarToggle>
                    <NavbarCollapse id="responsive-navbar-nav">
                        <Nav className="d-flex m-auto col-4">
                            <Form className="d-flex col-12">
                                {/* eslint-disable-next-line react/jsx-no-undef */}
                                <Form.Control
                                    type="search"
                                    placeholder="Search"
                                    className="me-3"
                                    aria-label="Search"
                                    style={{minWidth: "150px"}}
                                />
                                <Button variant="outline-dark" style={{backgroundColor: theme.peach}}>Search</Button>
                            </Form>
                        </Nav>

                        {nav}

                    </NavbarCollapse>


                </Container>
            </Navbar>


        </>
    );
};
