import {useLocation} from "react-router";
import songService from "../services/songService";
import {useEffect, useState} from "react";
import followPageService from "../services/followPageService";
import {Button, Card} from "react-bootstrap";
import Container from "react-bootstrap/Container";

export default function SongPage() {
    const {search} = useLocation();
    const [needUpdateReview, setNeedUpdateReview] = useState(false);
    const [needUpdateRating, setNeedUpdateRating] = useState(false);
    const [song, setSong] = useState()
    const params = new URLSearchParams(search);
    const songID = params.get('songID');

    useEffect(() => {
        songService.getSongDetail(songID).then(response => {
            console.log(response.data.result[0]);
            setSong(response.data.result[0])
        }).catch(e => console.log(e))


    }, [needUpdateReview, needUpdateRating]);

    return (
        <>
            <Container className="d-flex justify-content-start">


            <Card style={{ width: '500px', minHeight: '200px' }} className=" d-flex btn-outline-danger justify-content-center rounded-4 m-0" as={Button} >
                <Card.Body className="d-flex m-auto">
                    <Card.Title className="d-flex fs-2 text-start flex-grow-3 align-items-center" >{song && song.title}</Card.Title>
                    <Card.Subtitle className="mb-2 font-weight-light flex-grow-1">{song&& song.artist_list.join(", ")}</Card.Subtitle>

                </Card.Body>
            </Card>
            </Container>

        </>
    )
}
