import {useLocation} from "react-router";
import songService from "../services/songService";
import {useEffect, useState} from "react";
import followPageService from "../services/followPageService";
import {Button, Card, ListGroup} from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import Container from "react-bootstrap/Container";

export default function SongPage() {
    const {search} = useLocation();
    const [rate, setRate] = useState(3);
    const [needUpdateReview, setNeedUpdateReview] = useState(false);
    const [needUpdateRating, setNeedUpdateRating] = useState(false);
    const [song, setSong] = useState()
    const params = new URLSearchParams(search);
    const songID = params.get('songID');

    const showArtist = song && song.artist_list.length > 0;

    const showGenre = song && song.genre_list.length > 0;

    useEffect(() => {
        songService.getSongDetail(songID).then(response => {
            console.log(response.data.result[0]);
            setSong(response.data.result[0])
        }).catch(e => console.log(e))


    }, [needUpdateReview, needUpdateRating]);

    function handleRating(val) {
        console.log(Math.round(val / 20))
        setRate(Math.round(val / 20))
    }

    return (
        <>
            <Container className="d-flex justify-content-start flex-wrap">

                <Container className="d-flex flex-wrap  w-50  justify-content-center align-content-center" style={{width: '500px', minHeight: '200px', minWidth: '300px', height: '300px'}}>
                    <Card 
                          className=" d-flex btn-outline-danger justify-content-center rounded-4 m-0 mb-3 me-4"
                          as={Button}>
                        <Card.Body className="d-flex m-auto flex-wrap">
                            <Card.Title
                                className="d-flex fs-2 text-start  align-items-center">{song && song.title}</Card.Title>
                        </Card.Body>
                    </Card>
                </Container>

                <Container className="d-flex flex-wrap flex-column w-50  justify-content-center align-content-center">
                    <Card className=" mt-0 rounded-4" border="light" bg="white" style={{minWidth: '300px'}}>
                        <Card.Body>
                            <ListGroup as="ol" variant="flush">
                                <ListGroup.Item as="li"
                                                className="h5">Artist: {showArtist ? song.artist_list.join(",") : "Unknown"}</ListGroup.Item>
                                <ListGroup.Item as="li"
                                                className="h5">Genre: {showGenre ? song.genre_list.join(",") : "Unknown"}</ListGroup.Item>
                                <ListGroup.Item as="li" className="h5">Release
                                    Date: {song && song.releaseDate}</ListGroup.Item>
                            </ListGroup>
                        </Card.Body>
                    </Card>
                    <Container className="d-flex  justify-content-center">
                        <Card className=" m-3 rounded-4" border="light" bg="white" style={{minWidth: '300px'}}>
                            <Card.Body>
                                Avg Rating:
                                <Form.Range defaultValue={rate * 20} disabled={true}/>
                            </Card.Body>
                        </Card>
                    </Container>
                </Container>

                <Container className="d-flex  justify-content-center">
                    <Card className=" m-3 rounded-4" border="light" bg="white" style={{minWidth: '300px'}}>
                        <Card.Body>
                            Your Rating:
                            <Form.Range value={rate * 20} onChange={(e) => handleRating(e.target.value)}/>
                        </Card.Body>
                    </Card>
                </Container>


            </Container>


        </>
    )
}
